t.script:
                let count = 1;

                const container = document.getElementById('pet-container');

                function updateBadges() {
                    Array.from(container.children).forEach((card, index) => {
                        const badge = card.querySelector('.badge');
                        if (badge) {
                            badge.textContent = 'Pet ' + (index + 1);
                        }
                    });
                    count = container.children.length;
                }

                document.getElementById('add-pet').addEventListener('click', function() {
                    const container = document.getElementById('pet-container');
                    const newPet = container.children[0].cloneNode(true);

                    // Clear the values
                    newPet.querySelectorAll('input, select, textarea').forEach(function(el) {
                        if (el.type === 'checkbox') el.checked = false;
                        else el.value = '';
                    });

                    // Update badge number
                    container.appendChild(newPet);
                    updateBadges();
                });

                container.addEventListener('click', function(e) {
                    if (e.target.classList.contains('remove-pet')) {
                        const cards = container.children;
                        if (cards.length > 1) {
                            e.target.closest('.pet-entry').remove();
                            updateBadges();
                        } else {
                            alert('At least one pet must be entered.');
                        }
                    }
                });
